import React, { useState } from 'react';

const BookPickupScheduler = ({ book }) => {
  const [location, setLocation] = useState('');
  const [date, setDate] = useState('');
  const [timeSlot, setTimeSlot] = useState('');
  const [confirmation, setConfirmation] = useState(false);

  const locations = ['Library 1', 'Library 2', 'Center A', 'Center B', 'Bookstore C', 'Bookstore D'];
  const timeSlots = ['10:00 AM - 11:00 AM', '11:00 AM - 12:00 PM', '12:00 PM - 1:00 PM'];

  const handleSchedulePickup = () => {
    if (location && date && timeSlot) {
      setConfirmation(true);
    } else {
      alert('Please select all options before proceeding.');
    }
  };

  return (
    <div>
      {!confirmation ? (
        <div>
          <h2>Schedule Book Pickup</h2>
          <label>
            Select Pickup Location:
            <select value={location} onChange={(e) => setLocation(e.target.value)}>
              <option value="">Select Location</option>
              {locations.map((loc, index) => (
                <option key={index} value={loc}>
                  {loc}
                </option>
              ))}
            </select>
          </label>

          <label>
            Select Date:
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
          </label>

          <label>
            Select Time Slot:
            <select value={timeSlot} onChange={(e) => setTimeSlot(e.target.value)}>
              <option value="">Select Time Slot</option>
              {timeSlots.map((slot, index) => (
                <option key={index} value={slot}>
                  {slot}
                </option>
              ))}
            </select>
          </label>

          <button onClick={handleSchedulePickup}>Schedule Pickup</button>
        </div>
      ) : (
        <div>
          <h2>Pickup Confirmation</h2>
          <p>Book Title: {book.title}</p>
          <p>Pickup Location: {location}</p>
          <p>Pickup Date: {date}</p>
          <p>Pickup Time: {timeSlot}</p>
          <button onClick={() => alert('Pickup Scheduled!')}>Confirm Pickup</button>
        </div>
      )}
    </div>
  );
};

export default BookPickupScheduler;
