import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import axios from 'axios';
import './BookPickup.css';

const BookPickup = () => {
  const [pickupLocation, setPickupLocation] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [timeSlot, setTimeSlot] = useState('');
  const [confirmation, setConfirmation] = useState(false);
  const [bookDetails, setBookDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchBookDetails = async () => {
      setIsLoading(true);
      try {
        // Get the bookId from the URL query parameters
        const urlParams = new URLSearchParams(window.location.search);
        const bookId = urlParams.get('bookId');

        // Check if bookId is valid
        if (!bookId) {
          throw new Error('Book ID is missing in the URL.');
        }

        // Fetch the book details using the bookId
        const response = await axios.get(`/api/book/${bookId}`);
        setBookDetails(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching book details:', error.message || error);
        setIsLoading(false);
      }
    };

    fetchBookDetails();
  }, []);

  const handleSchedulePickup = async () => {
    if (pickupLocation && selectedDate && timeSlot) {
      try {
        const urlParams = new URLSearchParams(window.location.search);
        const bookId = urlParams.get('bookId');  // Get the bookId from URL query params

        const user = JSON.parse(localStorage.getItem('user'));
        await axios.post('/api/pickup/schedule', {
          bookId,
          email: user.email,
          pickupDate: selectedDate,
          pickupTime: timeSlot,
          pickupLocation: pickupLocation
        });
        setConfirmation(true);
      } catch (error) {
        console.error('Error scheduling pickup:', error);
      }
    } else {
      alert('Please select all options before proceeding.');
    }
  };

  if (isLoading) return <p>Loading book details...</p>;

  return (
    <div className="book-pickup-container">
      {!confirmation ? (
        <div>
          <h2>Schedule Book Pickup</h2>

          {bookDetails ? (
            <>
              <p>Book Title: {bookDetails.title}</p>
              <img src={bookDetails.image} alt={bookDetails.title} className="book-image" />
            </>
          ) : (
            <p>Failed to load book details.</p>
          )}

          <label>
            Select Pickup Location:
            <select value={pickupLocation} onChange={(e) => setPickupLocation(e.target.value)}>
              <option value="">Select Location</option>
              <option value="Library 1">Library 1</option>
              <option value="Library 2">Library 2</option>
              <option value="Center A">Center A</option>
              <option value="Center B">Center B</option>
            </select>
          </label>
          <br />
          <br />
          <label>
            Select Date:
            <DatePicker
              selected={selectedDate}
              onChange={(date) => setSelectedDate(date)}
              inline
              minDate={new Date()} // Prevent selecting past dates
            />
          </label>
          <br />
          <br />
          <label>
            Select Time Slot:
            <div className="time-slot-container">
              {['9:00 AM - 9:30 AM', '9:30 AM - 10:00 AM', '10:00 AM - 10:30 AM'].map((slot, index) => (
                <button
                  key={index}
                  className={`time-slot ${timeSlot === slot ? 'selected' : ''}`}
                  onClick={() => setTimeSlot(slot)}
                >
                  {slot}
                </button>
              ))}
            </div>
          </label>

          <button className="schedule-btn" onClick={handleSchedulePickup}>Schedule Pickup</button>
        </div>
      ) : (
        <div>
          <h2>Pickup Confirmation</h2>
          {bookDetails && (
            <>
              <p>Book Title: {bookDetails.title}</p>
              <img src={bookDetails.image} alt={bookDetails.title} className="book-image" />
            </>
          )}
          <p>Pickup Location: {pickupLocation}</p>
          <p>Pickup Date: {selectedDate?.toLocaleDateString()}</p>
          <p>Pickup Time: {timeSlot}</p>
          <button onClick={() => alert('Pickup Scheduled!')}>Confirm Pickup</button>
        </div>
      )}
    </div>
  );
};

export default BookPickup;
