import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import axios from 'axios';
import './BookPickup.css';

const BookPickup = () => {
  const [pickupLocation, setPickupLocation] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [timeSlot, setTimeSlot] = useState('');
  const [pickupScheduled, setPickupScheduled] = useState(false); // Track if a pickup is already scheduled

  // Fetch the user's scheduled pickup when the page loads
  useEffect(() => {
    const fetchScheduledPickup = async () => {
      const urlParams = new URLSearchParams(window.location.search);
      const cartId = urlParams.get('cartId');  // Get cartId from the URL

      try {
        const response = await axios.get('/api/pickup/scheduled', { params: { cartId } });

        if (response.data.scheduledPickup) {
          const pickup = response.data.scheduledPickup;
          setPickupLocation(pickup.pickupLocation);
          setSelectedDate(new Date(pickup.pickupDate));
          setTimeSlot(pickup.pickupTime);
          setPickupScheduled(true);  // Set to true if a pickup is already scheduled
        }
      } catch (error) {
        console.error('Error fetching scheduled pickup:', error);
      }
    };

    fetchScheduledPickup();
  }, []);

  // Fetch available slots when date and location are selected
  useEffect(() => {
    if (selectedDate && pickupLocation) {
      const fetchAvailableSlots = async () => {
        try {
          const response = await axios.get('/api/pickup/available-slots', {
            params: { pickupDate: selectedDate.toISOString().split('T')[0], pickupLocation }
          });
          setAvailableSlots(response.data.availableSlots);
        } catch (error) {
          console.error('Error fetching available slots:', error);
        }
      };

      fetchAvailableSlots();
    }
  }, [selectedDate, pickupLocation]);

  // Handle the scheduling of the pickup
  const handleSchedulePickup = async () => {
    if (pickupLocation && selectedDate && timeSlot) {
      try {
        const urlParams = new URLSearchParams(window.location.search);
        const cartId = urlParams.get('cartId');
        const user = JSON.parse(localStorage.getItem('user')); // Get user info from localStorage

        await axios.post('/api/pickup/schedule', {
          cartId,
          email: user.email,
          pickupDate: selectedDate,
          pickupTime: timeSlot,
          pickupLocation
        });

        setPickupScheduled(true); // Mark pickup as scheduled
      } catch (error) {
        console.error('Error scheduling pickup:', error);
      }
    } else {
      alert('Please select all options before proceeding.');
    }
  };

  // Conditionally render the reschedule view or scheduling view
  return (
    <div className="book-pickup-container">
      {pickupScheduled ? (
        <div>
          <h2>Reschedule Book Pickup</h2>
          <p>Pickup Location: {pickupLocation}</p>
          <p>Pickup Date: {selectedDate?.toLocaleDateString()}</p>
          <p>Pickup Time: {timeSlot}</p>
          <button className="reschedule-btn" onClick={() => setPickupScheduled(false)}>
            Reschedule Pickup
          </button>
        </div>
      ) : (
        <div>
          <h2>Schedule Book Pickup</h2>

          <label>
            Select Pickup Location:
            <select value={pickupLocation} onChange={(e) => setPickupLocation(e.target.value)}>
              <option value="">Select Location</option>
              <option value="Library 1">Library 1</option>
              <option value="Library 2">Library 2</option>
              <option value="Center A">Center A</option>
              <option value="Center B">Center B</option>
            </select>
          </label>
          <br />
          <br />
          <label>
            Select Date:
            <DatePicker
              selected={selectedDate}
              onChange={(date) => setSelectedDate(date)}
              minDate={new Date()}
            />
          </label>
          <br />
          <br />
          {availableSlots.length > 0 && (
            <label>
              Select Time Slot:
              <div className="time-slot-container">
                {availableSlots.map((slot, index) => (
                  <button
                    key={index}
                    className={`time-slot ${timeSlot === slot ? 'selected' : ''}`}
                    onClick={() => setTimeSlot(slot)}
                  >
                    {slot}
                  </button>
                ))}
              </div>
            </label>
          )}

          <button className="schedule-btn" onClick={handleSchedulePickup}>
            Schedule Pickup
          </button>
        </div>
      )}
    </div>
  );
};

export default BookPickup;
