import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import axios from 'axios';
import './BookPickup.css';  // Assuming you'll style it with a custom CSS file for a better UI

const BookPickup = () => {
  const [pickupLocation, setPickupLocation] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [timeSlot, setTimeSlot] = useState('');
  const [confirmation, setConfirmation] = useState(false);
  const [bookDetails, setBookDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(true);  // Added loading state

  useEffect(() => {
    const fetchBookDetails = async () => {
      setIsLoading(true);  // Start loading
      try {
        const user = JSON.parse(localStorage.getItem('user'));
        if (user && user.email) {
          const isbn = new URLSearchParams(window.location.search).get('isbn');  // Extract ISBN from query params
          const response = await axios.get(`/api/books/${encodeURIComponent(isbn)}`);
          setBookDetails(response.data);  // Set book details
        }
        setIsLoading(false);  // Stop loading
      } catch (error) {
        console.error("Error fetching book details:", error);
        setIsLoading(false);  // Stop loading on error
      }
    };

    fetchBookDetails();
  }, []);

  const locations = ['Library 1', 'Library 2', 'Center A', 'Center B', 'Bookstore C', 'Bookstore D'];

  const generateTimeSlots = () => {
    const timeSlots = [];
    let startTime = new Date();
    startTime.setHours(9, 0, 0, 0); // Start from 9 AM

    while (startTime.getHours() < 17) { // Till 5 PM
      const endTime = new Date(startTime);
      endTime.setMinutes(startTime.getMinutes() + 30); // Increment by 30 mins
      const formattedTime = `${startTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} - ${endTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
      timeSlots.push(formattedTime);
      startTime = endTime;
    }

    return timeSlots;
  };

  const handleSchedulePickup = async () => {
    if (pickupLocation && selectedDate && timeSlot) {
      try {
        const user = JSON.parse(localStorage.getItem('user'));
        const isbn = new URLSearchParams(window.location.search).get('isbn');  // Get ISBN from query params
        await axios.post('/api/pickup/schedule', {
          isbn,
          email: user.email,
          pickupDate: selectedDate,
          pickupTime: timeSlot,
          pickupLocation: pickupLocation
        });
        setConfirmation(true);
      } catch (error) {
        console.error('Error scheduling pickup:', error);
      }
    } else {
      alert('Please select all options before proceeding.');
    }
  };

  if (isLoading) return <p>Loading book details...</p>;

  return (
    <div className="book-pickup-container">
      {!confirmation ? (
        <div>
          <h2>Schedule Book Pickup</h2>

          {bookDetails ? (
            <>
              <p>Book Title: {bookDetails.title}</p>
              <img src={bookDetails.image} alt={bookDetails.title} className="book-image" />
            </>
          ) : (
            <p>Failed to load book details.</p>
          )}

          <label>
            Select Pickup Location:
            <select value={pickupLocation} onChange={(e) => setPickupLocation(e.target.value)}>
              <option value="">Select Location</option>
              {locations.map((loc, index) => (
                <option key={index} value={loc}>
                  {loc}
                </option>
              ))}
            </select>
          </label>
          <br />
          <br />
          <label>
            Select Date:
            <DatePicker
              selected={selectedDate}
              onChange={(date) => setSelectedDate(date)}
              inline
              minDate={new Date()} // Prevent selecting past dates
            />
          </label>
          <br />
          <br />
          <label>
            Select Time Slot:
            <div className="time-slot-container">
              {generateTimeSlots().map((slot, index) => (
                <button
                  key={index}
                  className={`time-slot ${timeSlot === slot ? 'selected' : ''}`}
                  onClick={() => setTimeSlot(slot)}
                >
                  {slot}
                </button>
              ))}
            </div>
          </label>

          <button className="schedule-btn" onClick={handleSchedulePickup}>Schedule Pickup</button>
        </div>
      ) : (
        <div>
          <h2>Pickup Confirmation</h2>
          {bookDetails && (
            <>
              <p>Book Title: {bookDetails.title}</p>
              <img src={bookDetails.image} alt={bookDetails.title} className="book-image" />
            </>
          )}
          <p>Pickup Location: {pickupLocation}</p>
          <p>Pickup Date: {selectedDate.toLocaleDateString()}</p>
          <p>Pickup Time: {timeSlot}</p>
          <button onClick={() => alert('Pickup Scheduled!')}>Confirm Pickup</button>
        </div>
      )}
    </div>
  );
};

export default BookPickup;
